	public function buildCriteria()
	{
		$criteria = new Criteria(CompraPeer::DATABASE_NAME);

		if ($this->isColumnModified(CompraPeer::ID)) $criteria->add(CompraPeer::ID, $this->id);
		if ($this->isColumnModified(CompraPeer::PRODUCTO_ID)) $criteria->add(CompraPeer::PRODUCTO_ID, $this->producto_id);
		if ($this->isColumnModified(CompraPeer::CANTIDAD)) $criteria->add(CompraPeer::CANTIDAD, $this->cantidad);
		if ($this->isColumnModified(CompraPeer::PROVEEDOR_ID)) $criteria->add(CompraPeer::PROVEEDOR_ID, $this->proveedor_id);
		if ($this->isColumnModified(CompraPeer::NOTA_PEDIDO_ID)) $criteria->add(CompraPeer::NOTA_PEDIDO_ID, $this->nota_pedido_id);
		if ($this->isColumnModified(CompraPeer::PRECIO)) $criteria->add(CompraPeer::PRECIO, $this->precio);
		if ($this->isColumnModified(CompraPeer::MONEDA)) $criteria->add(CompraPeer::MONEDA, $this->moneda);
		if ($this->isColumnModified(CompraPeer::FECHA)) $criteria->add(CompraPeer::FECHA, $this->fecha);
		if ($this->isColumnModified(CompraPeer::FECHA_ENTREGA)) $criteria->add(CompraPeer::FECHA_ENTREGA, $this->fecha_entrega);
		if ($this->isColumnModified(CompraPeer::COMENTARIO)) $criteria->add(CompraPeer::COMENTARIO, $this->comentario);
		if ($this->isColumnModified(CompraPeer::CREATED_AT)) $criteria->add(CompraPeer::CREATED_AT, $this->created_at);

		return $criteria;
