	public function getProducto(PropelPDO $con = null)
	{
		if ($this->aProducto === null && ($this->producto_id !== null)) {
			$c = new Criteria(ProductoPeer::DATABASE_NAME);
			$c->add(ProductoPeer::ID, $this->producto_id);
