					$obj3 = new $cls();
					$obj3->hydrate($row, $startcol3);
					sfGuardUserPeer::addInstanceToPool($obj3, $key3);
				} // if $obj3 already loaded

				// Add the $obj1 (CompraEstado) to the collection in $obj3 (sfGuardUser)
				$obj3->addCompraEstado($obj1);

			} // if joined row is not null

				// Add objects for joined Estado rows

				$key4 = EstadoPeer::getPrimaryKeyHashFromRow($row, $startcol4);
				if ($key4 !== null) {
					$obj4 = EstadoPeer::getInstanceFromPool($key4);
					if (!$obj4) {
	
						$omClass = EstadoPeer::getOMClass();


					$cls = substr('.'.$omClass, strrpos('.'.$omClass, '.') + 1);
					$obj4 = new $cls();
					$obj4->hydrate($row, $startcol4);
					EstadoPeer::addInstanceToPool($obj4, $key4);
				} // if $obj4 already loaded

				// Add the $obj1 (CompraEstado) to the collection in $obj4 (Estado)
				$obj4->addCompraEstado($obj1);

			} // if joined row is not null

			$results[] = $obj1;
		}
		$stmt->closeCursor();
		return $results;
	}


