	public static function retrieveByPK($pk, PropelPDO $con = null)
	{

		if (null !== ($obj = CompraEstadoPeer::getInstanceFromPool((string) $pk))) {
			return $obj;
		}

		if ($con === null) {
			$con = Propel::getConnection(CompraEstadoPeer::DATABASE_NAME, Propel::CONNECTION_READ);
		}

		$criteria = new Criteria(CompraEstadoPeer::DATABASE_NAME);
		$criteria->add(CompraEstadoPeer::ID, $pk);

		$v = CompraEstadoPeer::doSelect($criteria, $con);

		return !empty($v) > 0 ? $v[0] : null;
	}

