	 * @return     array Array of Venta objects.
	 * @throws     PropelException Any exceptions caught during processing will be
	 *		 rethrown wrapped into a PropelException.
	 */
	public static function doSelectJoinAllExceptsfGuardUser(Criteria $c, $con = null, $join_behavior = Criteria::LEFT_JOIN)
	{
		$c = clone $c;

		// Set the correct dbName if it has not been overridden
		// $c->getDbName() will return the same object if not set to another value
		// so == check is okay and faster
		if ($c->getDbName() == Propel::getDefaultDB()) {
			$c->setDbName(self::DATABASE_NAME);
		}

		VentaPeer::addSelectColumns($c);
		$startcol2 = (VentaPeer::NUM_COLUMNS - VentaPeer::NUM_LAZY_LOAD_COLUMNS);

		ProductoPeer::addSelectColumns($c);
		$startcol3 = $startcol2 + (ProductoPeer::NUM_COLUMNS - ProductoPeer::NUM_LAZY_LOAD_COLUMNS);

		ProveedorPeer::addSelectColumns($c);
		$startcol4 = $startcol3 + (ProveedorPeer::NUM_COLUMNS - ProveedorPeer::NUM_LAZY_LOAD_COLUMNS);

				$c->addJoin(array(VentaPeer::PRODUCTO_ID,), array(ProductoPeer::ID,), $join_behavior);
				$c->addJoin(array(VentaPeer::TRANSPORTISTA_EXTERNO_ID,), array(ProveedorPeer::ID,), $join_behavior);

		$stmt = BasePeer::doSelect($c, $con);
		$results = array();

		while ($row = $stmt->fetch(PDO::FETCH_NUM)) {
			$key1 = VentaPeer::getPrimaryKeyHashFromRow($row, 0);
			if (null !== ($obj1 = VentaPeer::getInstanceFromPool($key1))) {
				// We no longer rehydrate the object, since this can cause data loss.
				// See http://propel.phpdb.org/trac/ticket/509
				// $obj1->hydrate($row, 0, true); // rehydrate
			} else {
				$omClass = VentaPeer::getOMClass();

				$cls = substr('.'.$omClass, strrpos('.'.$omClass, '.') + 1);
				$obj1 = new $cls();
				$obj1->hydrate($row);
				VentaPeer::addInstanceToPool($obj1, $key1);
			} // if obj1 already loaded

				// Add objects for joined Producto rows

				$key2 = ProductoPeer::getPrimaryKeyHashFromRow($row, $startcol2);
				if ($key2 !== null) {
					$obj2 = ProductoPeer::getInstanceFromPool($key2);
					if (!$obj2) {
	
						$omClass = ProductoPeer::getOMClass();


					$cls = substr('.'.$omClass, strrpos('.'.$omClass, '.') + 1);
					$obj2 = new $cls();
					$obj2->hydrate($row, $startcol2);
					ProductoPeer::addInstanceToPool($obj2, $key2);
				} // if $obj2 already loaded

				// Add the $obj1 (Venta) to the collection in $obj2 (Producto)
				$obj2->addVenta($obj1);

			} // if joined row is not null

				// Add objects for joined Proveedor rows

				$key3 = ProveedorPeer::getPrimaryKeyHashFromRow($row, $startcol3);
				if ($key3 !== null) {
					$obj3 = ProveedorPeer::getInstanceFromPool($key3);
					if (!$obj3) {
	
						$omClass = ProveedorPeer::getOMClass();


					$cls = substr('.'.$omClass, strrpos('.'.$omClass, '.') + 1);
					$obj3 = new $cls();
					$obj3->hydrate($row, $startcol3);
					ProveedorPeer::addInstanceToPool($obj3, $key3);
				} // if $obj3 already loaded

				// Add the $obj1 (Venta) to the collection in $obj3 (Proveedor)
				$obj3->addVenta($obj1);

			} // if joined row is not null

			$results[] = $obj1;
		}
		$stmt->closeCursor();
		return $results;
	}


